446→      function rebuildTimeline() {
447→        const spb = secPerBeat();
448→        const introBeats = clamp(parseInt(introOffsetBeatsEl.value || "0", 10), -9999, 9999);
449→        let t = introBeats * spb; // 前奏オフセット（拍）を秒に変換して適用
450→
451→        timeline = ITEMS.map((it, idx) => {
452→          const beats = clamp(parseInt(it.beats || 8, 10), 1, 999);
453→          const dur = beats * spb;
454→          const a = answers.get(it.key) ?? it.aDefault ?? "";
455→          const obj = { i: idx, key: it.key, q: it.q, a, beats, start: t, end: t + dur };
456→          t += dur;
457→          return obj;
458→        });
459→        renderTimelineTable();
460→
461→        const bpm = parseFloat(bpmEl.value || "126");
462→        const mult = parseFloat(beatMultiplierEl.value || "1");
463→        debugLine.textContent = `debug: BPM=${bpm} / beatMult=${mult} / secPerBeat=${spb.toFixed(4)} / introOffsetBeats=${introBeats} / timelineEnd=${(timeline[timeline.length - 1]?.end ?? 0).toFixed(2)}s`;
464→      }
