480→
481→      function escapeHtml(s) {
482→        return String(s).replace(/[&<>"']/g, m => ({ "&": "&amp;", "<": "&lt;", ">": "&gt;", '"': "&quot;", "'": "&#039;" }[m]));
483→      }
484→
485→      function setStatus(msg) { statusEl.textContent = msg; }
