487→      // === preview draw ===
488→      function drawFrame(tSec) {
489→        const W = cv.width, H = cv.height;
490→
491→        // background: 白固定（画像があればそれを表示）
492→        ctx.clearRect(0, 0, W, H);
493→        if (bgImg) {
494→          const r = Math.max(W / bgImg.width, H / bgImg.height);
495→          const dw = bgImg.width * r;
496→          const dh = bgImg.height * r;
497→          const dx = (W - dw) / 2;
498→          const dy = (H - dh) / 2;
499→          ctx.drawImage(bgImg, dx, dy, dw, dh);
500→        } else {
501→          ctx.fillStyle = "#00ff00";
502→          ctx.fillRect(0, 0, W, H);
503→        }
504→
505→        const fade = clamp(parseFloat(fadeSecEl.value) || 0.2, 0, 3);
506→
507→        // active item
508→        const active = timeline.find(it => tSec >= it.start && tSec < it.end);
509→        if (!active) return;
510→
511→        const localT = tSec - active.start;
512→        const dur = active.end - active.start;
513→
514→        let alpha = 1.0;
515→        if (fade > 0) {
516→          const fi = clamp(localT / fade, 0, 1);
517→          const fo = clamp((dur - localT) / fade, 0, 1);
518→          alpha = Math.min(fi, fo);
519→        }
520→
521→        const pad = Math.round(Math.min(W, H) * 0.04);
522→        const panelH = Math.round(H * 0.28);
523→        const panelY = H - panelH - pad;
524→
525→        // subtitle panel (黒透過)
526→        ctx.save();
527→        ctx.globalAlpha = 0.78 * alpha;
528→        ctx.fillStyle = "rgba(0,0,0,0.72)";
529→        roundRect(ctx, pad, panelY, W - pad * 2, panelH, 18);
530→        ctx.fill();
531→        ctx.restore();
532→
533→        const fontSize = clamp(parseInt(fontSizeEl.value || "54", 10), 18, 200);
534→        const qFont = Math.round(fontSize * 0.95);
535→        const aFont = Math.round(fontSize * 1.05);
536→
537→        // text
538→        ctx.save();
539→        ctx.globalAlpha = alpha;
540→        ctx.fillStyle = "white";
541→        ctx.textAlign = "center";
542→        ctx.textBaseline = "middle";
543→
544→        ctx.font = `700 ${qFont}px system-ui, -apple-system, "Segoe UI", "Noto Sans JP", sans-serif`;
545→        wrapText(ctx, active.q, W / 2, panelY + panelH * 0.38, W - pad * 3, qFont * 1.15);
546→
547→        const ans = (active.a && active.a.length) ? active.a : "（未入力）";
548→        ctx.font = `800 ${aFont}px system-ui, -apple-system, "Segoe UI", "Noto Sans JP", sans-serif`;
549→        wrapText(ctx, ans, W / 2, panelY + panelH * 0.72, W - pad * 3, aFont * 1.2);
550→
551→        ctx.restore();
552→      }
553→
554→      function roundRect(ctx, x, y, w, h, r) {
555→        const rr = Math.min(r, w / 2, h / 2);
556→        ctx.beginPath();
557→        ctx.moveTo(x + rr, y);
558→        ctx.arcTo(x + w, y, x + w, y + h, rr);
559→        ctx.arcTo(x + w, y + h, x, y + h, rr);
560→        ctx.arcTo(x, y + h, x, y, rr);
561→        ctx.arcTo(x, y, x + w, y, rr);
562→        ctx.closePath();
563→      }
564→
565→      function wrapText(ctx, text, x, y, maxWidth, lineHeight) {
566→        const s = String(text);
567→        // ざっくり分割（空白がない日本語にも対応）
568→        const words = s.includes(" ") ? s.split(/\s+/) : Array.from(s);
569→
570→        let lines = [];
571→        let line = "";
572→        const tryPush = (candidate) => ctx.measureText(candidate).width <= maxWidth;
573→
574→        const pushLine = () => { if (line.trim().length) lines.push(line.trim()); line = ""; };
575→
576→        for (const w of words) {
577→          const test = (line ? line + (s.includes(" ") ? " " : "") : "") + w;
578→          if (tryPush(test)) line = test;
579→          else { pushLine(); line = w; }
580→        }
581→        pushLine();
582→
583→        if (lines.length > 3) {
584→          lines = lines.slice(0, 3);
585→          const last = lines[2];
586→          lines[2] = last.length > 18 ? last.slice(0, 18) + "…" : last + "…";
587→        }
588→
589→        const totalH = lines.length * lineHeight;
590→        let yy = y - totalH / 2 + lineHeight / 2;
591→        for (const ln of lines) {
592→          ctx.save();
593→          ctx.fillStyle = "rgba(0,0,0,0.55)";
594→          ctx.fillText(ln, x + 2, yy + 2);
595→          ctx.restore();
596→          ctx.fillStyle = "white";
597→          ctx.fillText(ln, x, yy);
598→          yy += lineHeight;
599→        }
600→      }
601→
602→      // === media handling ===
603→      audioFile.addEventListener("change", () => {
604→        const f = audioFile.files?.[0];
605→        if (!f) return;
606→        const url = URL.createObjectURL(f);
607→        audioEl.src = url;
608→        audioEl.load();
609→        setStatus(`BGM読み込み: ${f.name}`);
610→      });
611→
612→      bgFile.addEventListener("change", async () => {
613→        const f = bgFile.files?.[0];
614→        if (!f) { bgImg = null; return; }
615→        const url = URL.createObjectURL(f);
616→        const img = new Image();
617→        img.onload = () => { bgImg = img; URL.revokeObjectURL(url); };
618→        img.src = url;
619→      });
620→
621→      resolutionEl.addEventListener("change", () => {
622→        const [w, h] = resolutionEl.value.split("x").map(n => parseInt(n, 10));
623→        cv.width = w; cv.height = h;
624→        drawFrame(0);
625→      });
626→
627→      applyTimelineBtn.addEventListener("click", () => {
628→        rebuildTimeline();
629→        drawFrame(0);
630→        setStatus("タイムラインを再計算しました。");
631→      });
632→
633→      // util: wait audio metadata
634→      function waitLoadedMetadata(mediaEl) {
635→        if (isFinite(mediaEl.duration) && mediaEl.duration > 0) return Promise.resolve();
636→        return new Promise((resolve, reject) => {
637→          const onOk = () => { cleanup(); resolve(); };
638→          const onErr = () => { cleanup(); reject(new Error("audio metadata load failed")); };
639→          const cleanup = () => {
640→            mediaEl.removeEventListener("loadedmetadata", onOk);
641→            mediaEl.removeEventListener("durationchange", onOk);
642→            mediaEl.removeEventListener("error", onErr);
643→          };
644→          mediaEl.addEventListener("loadedmetadata", onOk, { once: true });
645→          mediaEl.addEventListener("durationchange", onOk, { once: true });
646→          mediaEl.addEventListener("error", onErr, { once: true });
647→          mediaEl.load();
648→        });
649→      }
650→
651→      function waitSeconds(s) {
652→        return new Promise(res => setTimeout(res, Math.max(0, s) * 1000));
653→      }
654→
655→      function stopRecorder(rec, timeoutMs = 8000) {
656→        return new Promise((resolve, reject) => {
657→          const timer = setTimeout(() => {
658→            try { rec.requestData?.(); } catch { }
659→            reject(new Error("MediaRecorder stop timeout"));
660→          }, timeoutMs);
661→
662→          rec.addEventListener("stop", () => {
663→            clearTimeout(timer);
664→            resolve();
665→          }, { once: true });
666→
667→          try { rec.requestData?.(); } catch { }
668→          rec.stop();
669→        });
670→      }
671→
672→      // === render video ===
673→      async function renderVideo() {
674→        const f = audioFile.files?.[0];
675→        if (!f) { alert("BGMファイルを選んでください"); return; }
676→
677→        rebuildTimeline();
678→
679→        // ensure duration is available (prevents Infinity hang)
680→        await waitLoadedMetadata(audioEl);
681→
682→        const timelineEnd = timeline[timeline.length - 1]?.end ?? 0;
683→        const audioDur = (isFinite(audioEl.duration) && audioEl.duration > 0) ? audioEl.duration : timelineEnd;
684→        const stopMode = stopModeEl.value;
685→
686→        // Setup AudioContext mix -> MediaStreamDestination
687→        const ac = new (window.AudioContext || window.webkitAudioContext)();
688→        const src = ac.createMediaElementSource(audioEl);
689→        const gain = ac.createGain();
690→        gain.gain.value = 1.0;
691→        const dest = ac.createMediaStreamDestination();
692→        src.connect(gain).connect(dest);
693→        gain.connect(ac.destination); // monitor on speakers
694→
695→        const fps = clamp(parseInt(fpsEl.value || "30", 10), 10, 60);
696→        const stream = cv.captureStream(fps);
697→        const mixed = new MediaStream([stream.getVideoTracks()[0], dest.stream.getAudioTracks()[0]]);
698→
699→        const candidates = [
700→          'video/webm;codecs=vp9,opus',
701→          'video/webm;codecs=vp8,opus',
702→          'video/webm;codecs=vp9',
703→          'video/webm'
704→        ];
705→        let mimeType = "";
706→        for (const c of candidates) {
707→          if (MediaRecorder.isTypeSupported(c)) { mimeType = c; break; }
708→        }
709→        if (!mimeType) mimeType = "video/webm";
710→
711→        const rec = new MediaRecorder(mixed, { mimeType, videoBitsPerSecond: 6_000_000 });
712→        const chunks = [];
713→        rec.ondataavailable = (e) => { if (e.data && e.data.size) chunks.push(e.data); };
714→
715→        rec.addEventListener("start", () => console.log("[rec] start"));
716→        rec.addEventListener("dataavailable", (e) => console.log("[rec] chunk", e.data?.size));
717→        rec.addEventListener("stop", () => console.log("[rec] stop"));
718→
719→        const startedAt = performance.now();
720→        let rafId = 0;
721→        let stopped = false;
722→
723→        function tick() {
724→          const t = (performance.now() - startedAt) / 1000;
725→          drawFrame(t);
726→          if (!stopped) rafId = requestAnimationFrame(tick);
727→        }
728→
729→        console.log("[debug] audioDur=", audioDur, "timelineEnd=", timelineEnd, "stopMode=", stopMode);
730→        setStatus(`録画中… mimeType=${mimeType}`);
731→        downloadLink.style.display = "none";
732→        downloadLink.href = "";
733→
734→        rec.start(200);
735→        await ac.resume();
736→
737→        audioEl.currentTime = 0;
738→        await audioEl.play();
739→
740→        tick();
741→
742→        // Decide stopping condition (prevents "倍以上に伸びる" 事故)
743→        const tail = 0.25;
744→
745→        const waitAudioEnded = new Promise(res => audioEl.addEventListener("ended", res, { once: true }));
746→        const waitTimeline = waitSeconds(Math.max(0, timelineEnd + tail));
747→
748→        if (stopMode === "audio") {
749→          await waitAudioEnded;
750→        } else if (stopMode === "timeline") {
751→          await waitTimeline;
752→        } else {
753→          // race (earlier)
754→          await Promise.race([waitAudioEnded, waitTimeline]);
755→        }
756→
757→        stopped = true;
758→        cancelAnimationFrame(rafId);
759→        audioEl.pause();
760→
761→        await stopRecorder(rec, 10000);
762→        await ac.close().catch(() => { });
763→
764→        const blob = new Blob(chunks, { type: "video/webm" });
765→        const url = URL.createObjectURL(blob);
766→        downloadLink.href = url;
767→        downloadLink.style.display = "inline-block";
768→        setStatus(`完了！動画をダウンロードできます（音源 ${audioDur.toFixed(1)}s / timeline ${timelineEnd.toFixed(1)}s）`);
769→      }
770→
771→      renderBtn.addEventListener("click", async () => {
772→        renderBtn.disabled = true;
773→        try {
774→          await renderVideo();
775→        } catch (e) {
776→          console.error(e);
777→          alert("生成中にエラーが発生しました。\n" + (e?.message || e));
778→          setStatus("エラー：" + (e?.message || e));
779→        } finally {
780→          renderBtn.disabled = false;
781→        }
782→      });
783→
784→      // init
785→      buildQAUI();
786→      rebuildTimeline();
787→      drawFrame(0);
788→    })();
789→  </script>
790→</body>
