452→// ==========================================
453→function initCodeRain() {
454→  const container = document.getElementById('codeRain');
455→  const snippets = [
456→    'const app = () => {', 'return <div>', 'useState(0)', 'async fetch()',
457→    'npm run dev', '.then(res =>', 'export default', 'className="',
458→    'onClick={handle}', 'useEffect(() =>', '<Component />', 'git commit -m',
459→    'console.log()', 'import React', 'const [data]', 'await api.get',
460→    'function main()', 'border-radius', 'flex items-center', 'grid cols-3',
461→    'try { await', 'catch (err)', '} finally {', 'new Promise(',
462→  ];
463→
464→  const columns = Math.min(Math.floor(window.innerWidth / 80), 20);
465→  for (let i = 0; i < columns; i++) {
466→    const col = document.createElement('div');
467→    col.className = 'code-column';
468→    col.style.left = `${(i / columns) * 100}%`;
469→    col.style.setProperty('--duration', `${15 + Math.random() * 20}s`);
470→    col.style.setProperty('--delay', `${-Math.random() * 20}s`);
471→    col.style.opacity = 0.03 + Math.random() * 0.06;
472→
473→    let text = '';
474→    const lineCount = 20 + Math.floor(Math.random() * 15);
475→    for (let j = 0; j < lineCount; j++) {
476→      text += snippets[Math.floor(Math.random() * snippets.length)] + '\n';
477→    }
478→    col.textContent = text;
479→    container.appendChild(col);
480→  }
481→}
