483→// ==========================================
484→// COUNTER ANIMATION
485→// ==========================================
486→function animateCounter(elementId, target, duration = 1500) {
487→  const el = document.getElementById(elementId);
488→  if (!el) return;
489→  const start = performance.now();
490→  const startVal = 0;
491→
492→  function update(now) {
493→    const elapsed = now - start;
494→    const progress = Math.min(elapsed / duration, 1);
495→    const eased = 1 - Math.pow(1 - progress, 3); // ease-out cubic
496→    const current = Math.floor(startVal + (target - startVal) * eased);
497→    el.textContent = current.toLocaleString();
498→    if (progress < 1) requestAnimationFrame(update);
499→  }
500→  requestAnimationFrame(update);
501→}
