609→function initReveal() {
610→  const observer = new IntersectionObserver((entries) => {
611→    entries.forEach(entry => {
612→      if (entry.isIntersecting) {
613→        entry.target.classList.add('visible');
614→      }
615→    });
616→  }, { threshold: 0.1 });
617→
618→  document.querySelectorAll('.reveal').forEach(el => observer.observe(el));
619→}
