import { chromium } from 'playwright';
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

async function main() {
  const browser = await chromium.launch();
  const page = await browser.newPage({ viewport: { width: 1280, height: 720 } });

  // Screenshot the dark version (archived copy)
  const darkPath = 'file://' + path.join(__dirname, 'index-v1-dark.html');
  console.log('Taking dark version screenshot...');
  await page.goto(darkPath, { waitUntil: 'load' });
  await page.waitForTimeout(1000);
  await page.screenshot({
    path: path.join(__dirname, 'screenshots/archive/v1-dark-full.png'),
    fullPage: true,
  });
  console.log('Done: v1-dark-full.png');

  await browser.close();
}

